sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
	c.notePos = 0;

	c.selectItem = function(idx){
		c.notePos = idx;
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
    $rootScope.noteID = id;
    $rootScope.$emit('selectNote', id);
	}

	$rootScope.$on('updateList', function(event,data) { 
		console.log(c);
		c.data.notes[c.notePos].title = data.title;
		c.data.notes[c.notePos].desc = data.desc;
	});
};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	grN = new GlideRecord('x_snc_createnotes_note');
	grN.addQuery('user', gs.getUser().getID());
	grN.orderByDesc('sys_created_on');
	grN.query();

	while(grN.next()){
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, grN, 'number,title,sys_id');
		noteObj.desc = grN.getDisplayValue('note').substr(0,20);
		data.notes.push(noteObj);
	}
})();
